uint32_t enclave_create_report(const sgx_target_info_t *p_qe3_target, sgx_report_t *p_report)
{

    uint32_t ret = 0;
    sgx_status_t sgx_status;
    sgx_report_data_t report_data = {0};
    sgx_sha_state_handle_t sha_handle = NULL;
    uint8_t crypto_suite = PCE_ALG_RSA_OAEP_3072;

    // SHA256(crypto_suite||public_key)
    do
    {
        sgx_status = sgx_sha256_init(&sha_handle);
        if (SGX_SUCCESS != sgx_status)
        {
            break;
        }
        sgx_status = sgx_sha256_update(&crypto_suite,
                                       sizeof(uint8_t),
                                       sha_handle);
        if (SGX_SUCCESS != sgx_status)
        {
            return sgx_status;
        }
        sgx_status = sgx_sha256_update(INTEL_PPIDEK,
                                       PUBLIC_KEY_SIZE,
                                       sha_handle);
        sgx_status = sgx_sha256_get_hash(sha_handle, reinterpret_cast<sgx_sha256_hash_t *>(&report_data));
    } while (0);

    if (sha_handle != NULL)
    {
        sgx_sha256_close(sha_handle);
    }

    // Generate the report for the app_enclave
    sgx_status_t sgx_error = sgx_create_report(p_qe3_target, &report_data, p_report);
    return sgx_error;
}